<?phpif (!defined('IN_UOKE')) {    exit('Wrong!!');}class Model_Project extends Model {    public $getInput = array();    protected $pageNum = '10';    protected $defaultTemplate = 'admin/project';    protected $modelUriRewrite = array(    );    protected $privateUri = array(        'projectWatchUri' => 'adminProject.php?act=watch&baseId=',        'projectClientListUri' => 'adminProject.php?act=clientlist&baseId=',        'projectEditUri' => 'adminProject.php?act=editProject',    );    public function list_Action() {        list(,$projectList) = control_baseMoney::getList($this->getInput['page'], 20);        $this->modelDisplay(array('projectList' => $projectList));    }        public function watch_Action() {        $baseId = intval($this->getInput['baseId']);        $baseInfo = control_baseMoney::getBaseInfo($baseId);        $this->modelDisplay(array('baseId' => $baseId, 'baseInfo' => $baseInfo));    }        public function editProject_Action() {        if (app::v('checkallow', 'submit')) {            $baseId = intval($this->getInput['baseId']);            $moneyInfo = array(                control_baseMoney::TABLE_BASEMONEY => $this->getInput[control_baseMoney::TABLE_BASEMONEY],                control_baseMoney::TABLE_RUNMONEY => $this->getInput[control_baseMoney::TABLE_RUNMONEY],                control_baseMoney::TABLE_CLIENTMONEY => $this->getInput[control_baseMoney::TABLE_CLIENTMONEY]            );            control_baseMoney::editBaseMoneyInfo($moneyInfo, $baseId);            showmsg(control_returnCode::FORM_SUBMIT_TRUE);        }    }        public function clientlist_Action() {        $baseId = intval($this->getInput['baseId']);        $baseInfo = control_baseMoney::getBaseInfo($baseId);        list(,$clientList) = control_userBase::getPlanUserHaveList($baseId);        foreach($clientList as $client) {            $uids[] = $client[control_userBase::TABLE_USERID];        }                $clientMoreInfoList = control_user::getUserListByIds($uids);                $this->modelDisplay(array('baseId' => $baseId, 'baseInfo' => $baseInfo, 'clientList' => $clientList, 'clientInfo' => $clientMoreInfoList));    }            }